INSERT INTO `ebook` (`ebook_id`, `user_id`, `category_id`, `title`, `description`, `thumbnail`, `banner`, `file`, `publication_name`, `edition`, `discount_flag`, `discounted_price`, `price`, `added_date`, `updated_date`, `is_active`, `is_free`, `preview`) VALUES ('1', '1', '1', 'Private: Antimicrobial Guidelines – Workbook', '&lt;p style=&quot;margin-right: 0px; margin-bottom: 30px; margin-left: 0px; color: rgb(85, 85, 85); font-family: &amp;quot;Open Sans&amp;quot;;&quot;&gt;This E-booklet aims to develop your skills in :&lt;/p&gt;&lt;ul style=&quot;margin-bottom: 10px; padding-left: 20px; color: rgb(85, 85, 85); font-family: &amp;quot;Open Sans&amp;quot;;&quot;&gt;&lt;li style=&quot;margin-bottom: 8px;&quot;&gt;Explore some of the current prescribing practices and legislature around prescribing.&lt;/li&gt;&lt;li style=&quot;margin-bottom: 8px;&quot;&gt;Provides an overview of guidelines around treating infections presenting in primary care.&lt;/li&gt;&lt;li style=&quot;margin-bottom: 8px;&quot;&gt;Demonstrates the principles and application of appropriate antimicrobial use.&lt;/li&gt;&lt;li style=&quot;margin-bottom: 8px;&quot;&gt;Understand the importance of using local guidelines and an evidence based approach for antimicrobial stewardship in your practice setting.&lt;/li&gt;&lt;/ul&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 30px; margin-left: 0px; color: rgb(85, 85, 85); font-family: &amp;quot;Open Sans&amp;quot;;&quot;&gt;Type: Downloadable PDF file.&lt;/p&gt;&lt;p style=&quot;margin-right: 0px; margin-bottom: 30px; margin-left: 0px; color: rgb(85, 85, 85); font-family: &amp;quot;Open Sans&amp;quot;;&quot;&gt;Composed by Jeshni Amblum-Almér&lt;/p&gt;', '9762bda8728cc9ba12a2a887c810db82.jpg', '479d6d04df6de36c80fd3e1a67456e85.jpg', 'cc4fa89b6bb97aa7fc44ea273a5af15d.pdf', 'Belmatt', 'V1', '0', '0', '18', '1698206400', NULL, '1', '0', NULL);
